tinymce.PluginManager.add('mediamanager', function(editor, url) {
	
    // Adds a menu item to the insert menu
    editor.addMenuItem('mediamanager', {
        text: '视频',
        context: 'insert',
		icon: 'media',
        onclick: function() {
			show_tinymce_modal("show_addvideo");
        }
    });
});